import { useState } from "react";
import "./App.css";

const neckOptions = [
  {
    id: "round",
    title: "Round Neck",
    subtitle: "Classic and versatile",
    icon: "‚≠ï",
  },
  {
    id: "vneck",
    title: "V-Neck",
    subtitle: "Elegant and elongating",
    icon: "üîª",
  },
  {
    id: "boat",
    title: "Boat Neck",
    subtitle: "Wide and sophisticated",
    icon: "‚õµ",
  },
  {
    id: "high",
    title: "High Neck",
    subtitle: "Modern and chic",
    icon: "‚õ∞Ô∏è",
  },
  {
    id: "scoop",
    title: "Scoop Neck",
    subtitle: "Soft and feminine",
    icon: "ü•Ñ",
  },
  {
    id: "square",
    title: "Square Neck",
    subtitle: "Structured and bold",
    icon: "‚¨ú",
  },
];

export default function NeckDesign() {
  const [selectedNeck, setSelectedNeck] = useState("vneck");

  return (
    <div className="container">
      <h2 className="heading">Choose Neck Design</h2>
      <p className="subheading">Select the perfect neckline for your outfit</p>

      <div className="grid">
        {neckOptions.map((neck) => (
          <div
            key={neck.id}
            className={`card ${
              selectedNeck === neck.id ? "active" : ""
            }`}
            onClick={() => setSelectedNeck(neck.id)}
          >
            <div className="icon">{neck.icon}</div>
            <h3>{neck.title}</h3>
            <p>{neck.subtitle}</p>
          </div>
        ))}
      </div>
        <Link to="/measurements" className="btn btn-primary mt-8 inline-block">
          Next: Measurements ‚Üí</Link>   

    </div>
  );
}
