import React, { useState } from "react";
import { Link } from "react-router-dom";
import "./App.css";

const fabrics = [
  {
    id: 1,
    name: "Cotton",
    desc: "Soft & breathable",
    color: "#E3F2FD",
  },
  {
    id: 2,
    name: "Silk",
    desc: "Luxury & smooth",
    color: "#FCE4EC",
  },
  {
    id: 3,
    name: "Linen",
    desc: "Light & airy",
    color: "#E8F5E9",
  },
  {
    id: 4,
    name: "Georgette",
    desc: "Flowy & elegant",
    color: "#FFF3E0",
  },
  {
    id: 5,
    name: "Chiffon",
    desc: "Sheer & graceful",
    color: "#F3E5F5",
  },
  {
    id: 6,
    name: "Denim",
    desc: "Strong & durable",
    color: "#ECEFF1",
  },
];

export default function Fabric() {
  const [selectedFabric, setSelectedFabric] = useState(null);

  return (
    <div className="page-container">
      <h2 className="page-title">Choose Your Fabric</h2>
      <p className="page-subtitle">
        Select one fabric that fits your comfort
      </p>

      <div className="card-grid">
        {fabrics.map((fabric) => (
          <div
            key={fabric.id}
            className={`card ${
              selectedFabric === fabric.id ? "selected" : ""
            }`}
            style={{ backgroundColor: fabric.color }}
            onClick={() => setSelectedFabric(fabric.id)}
          >
            <div className="check-circle">
              {selectedFabric === fabric.id && "✓"}
            </div>

            <h3>{fabric.name}</h3>
            <p>{fabric.desc}</p>
          </div>
        ))}
      </div>

      <div className="action-buttons">
        <Link to="/design" className="back-btn">
          ← Back
        </Link>

        <Link
          to="/design"
          className={`continue-btn ${
            !selectedFabric ? "disabled" : ""
          }`}
          onClick={(e) => !selectedFabric && e.preventDefault()}
        >
          Continue →
        </Link>
      </div>
    </div>
  );
}
