  { name: "Natural Linen", desc: "Light and airy", color: "#dcfce7" },
  { name: "Chiffon", desc: "Delicate and flowing", color: "#fce7f3" },
  { name: "Satin", desc: "Smooth and shiny", color: "#fef9c3" },
  { name: "Velvet", desc: "Rich and textured", color: "#fde2e2" },
];

